var addItem=document.querySelectorAll(".catalog-button"),add=document.querySelector(".modal-add"),addClose=document.querySelector(".add__close");addItem.forEach(function(e){e.addEventListener("click",function(e){e.preventDefault(),add.classList.add("modal__show")})}),addClose.addEventListener("click",function(e){e.preventDefault(),add.classList.remove("modal__show")}),window.addEventListener("keydown",function(e){27===e.keyCode&&(e.preventDefault(),add.classList.contains("modal__show")&&add.classList.remove("modal__show"))});
